public int[] FrequencySort(int[] nums)
{
	return nums
			.GroupBy(x => x)
			.OrderBy(x => x.Count())
			.ThenByDescending(x => x.Key)
			.SelectMany(x => Enumerable.Repeat(x.Key, x.Count()))
			.ToArray();
}
