    private static int[] frequencySort(int[] nums) {
        // nums -> [1,1,2,2,2,3]
        Map<Integer, Integer> numberToOccurances = new HashMap<>();
        for (Integer i : nums) {
            numberToOccurances.put(i, numberToOccurances.getOrDefault(i, 0) + 1);
        }
        // numberToOccurances-> {1:2},{2:3}, {3:1}
        Queue<Integer> heap = new PriorityQueue<>((e1, e2) -> numberToOccurances.get(e1).equals(numberToOccurances.get(e2)) ?
                e2 - e1 :
                numberToOccurances.get(e1) - numberToOccurances.get(e2));

        for (Integer i : numberToOccurances.keySet()) {
            heap.offer(i);
        }
        // heap -> 3, 1, 2 - according to frequencies 3 is met 1 time, 1 -> 2 times & 2 -> 3 times
        int[] result = new int[nums.length];
        int index = 0;
        while (!heap.isEmpty()) {
            Integer num = heap.poll();
            int iterations = numberToOccurances.get(num);
            for (int i = 1; i <= iterations; i++) {
                result[index] = num;
                index++;
            }

        }
        return result;
    }
