    public int[] frequencySort(int[] nums) {

		int[] count = new int[201];
		List<Integer>[] temp = new ArrayList[101];
		for (int i = 0; i < 101; i++)
			temp[i] = new ArrayList<Integer>();

		for (int num : nums)
			count[num + 100]++;

		for (int i = 0; i < 201; i++) {
			if (count[i] > 0)
				temp[count[i]].add(i - 100);
		}

		int idx = 0;
		for (int i = 0; i < 101; i++) {
			if (temp[i].size() != 0) {
				List<Integer> freq = temp[i];
				int len = freq.size();
				for (int j = len - 1; j >= 0; j--) {
					for (int k = 0; k < i; k++)
						nums[idx++] = freq.get(j);
				}
			}
		}

		return nums;
	}

