    public int[] frequencySort(int[] nums) {
        int[] freq = new int[201];
        for (int num : nums)
            freq[num+100]++;

        int n = nums.length;
        List<Integer>[] buckets = new List[n+1];
        for (int num = 0; num < 201; ++num) {
            if (freq[num] == 0) continue;

            if (buckets[freq[num]] == null)
                buckets[freq[num]] = new ArrayList<>();
            buckets[freq[num]].add(num-100);
        }

        int i = 0;
        for (int f = 1; f <= n; ++f) {
            if (buckets[f] == null) continue;

            for (int b = buckets[f].size()-1; b >= 0; --b) { // since we added elements in incresing order
                for (int j = 1; j <= f; ++j) 
                    nums[i++] = buckets[f].get(b);
            }
        }

        return nums;
    }
