class Solution {
    public int[] frequencySort(int[] nums) {

                int[] sorted = new int[nums.length];

        Map<Integer, Integer> numsMap = new HashMap<>();

        for(int num : nums) numsMap.put(num, numsMap.getOrDefault(num, 0)+1);

        PriorityQueue<Map.Entry<Integer, Integer>> pq = new PriorityQueue<Map.Entry<Integer, Integer>>(new Comparator<Map.Entry<Integer, Integer>>() {
            @Override
            public int compare(Map.Entry<Integer, Integer> e1, Map.Entry<Integer, Integer> e2) {
                int c = e1.getValue() - e2.getValue();
                if(c == 0) return e2.getKey() - e1.getKey();
                else return c;
            }
        });

        //for(Map.Entry<Integer, Integer> numsMapentry : numsMap.entrySet()) pq.add(numsMapentry);
        pq.addAll(numsMap.entrySet());
        int index = 0;
        while(!pq.isEmpty()) {
            Map.Entry<Integer, Integer> current = pq.poll();
            for(int i=0; i< current.getValue(); i++) sorted[index++] = current.getKey();
        }


        //System.out.println(numsMap);

        return sorted;
    }
}
