class Solution {
    public int[] frequencySort(int[] nums) {
        Map<Integer, Integer> m = new HashMap<>();
        List l<Integer> = new ArrayList();
        for (int i : nums){
            int oc = m.getOrDefault(i,0);
            l.add(i);
            m.put(i, ++oc);
        }

        l.sort((a, b) -> {
            int c = Integer.compare(m.get(a), m.get(b));
            if (c == 0) return Integer.compare((Integer) a, (Integer) b) * -1;
            return c;
        });
        for (int i = 0; i < l.size(); i++) {
            nums[i] = (Integer) l.get(i);
        }
        return nums;
    }
}
