```
class Solution {
public:
    void compute(int lps[],string pat){

         int n = pat.size();
         int i = 1;
         int len = 0;
         lps[0] = 0;
         while(i < n){

              if(pat[i] == pat[len]){

                    len += 1;
                    lps[i] = len;
                    i++;
              }
              else{

                    if(len != 0){

                        len = lps[len-1];
                    }
                    else{
                           lps[i] = 0;
                           i+=1;
                    }
              }
         }
    }

    bool check(string word,string pat){

          int lps[pat.size()];
          int i = 0, j = 0;

          compute(lps,pat);

          while(i<word.size()  &&  j < pat.size()){

                 if(word[i] == pat[j]){
                      i++;
                      j++;
                 }
                 if(j == pat.size()) return true;
                 if(i < word.size() && word[i] != pat[j]){

                       if(j != 0){
                             j = lps[j-1];
                       }
                       else{
                             i++;
                       }
                 }
          }
        return false;
    }

    int numOfStrings(vector<string>& patterns, string word) {
        int ans = 0;
        for(auto s : patterns){

             if(check(word,s)){

                  ans += 1;
             }
        }
        return ans;
    }
};
```

