class Solution:
    def numOfStrings(self, patterns: List[str], word: str) -> int:

        res = 0
        for pattern in patterns:
            winL = 0
            winR = len(pattern)

            while winR<=len(word):    
                if word[winL:winR] == pattern:
                    res+=1
                    break
                winL+=1
                winR+=1

        return res
