var numberOfLines = function(widths, s) {
    let currentSumPixels = 0;
    const output = [1, 0];
    for (let i = 0; i < s.length; i++) {
        const indexOfChar = s.charCodeAt(i) - 97;
        currentSumPixels += widths[indexOfChar];
        if (currentSumPixels > 100) {
            currentSumPixels = 0;
            output[0]++;
            i--;
        } else if (currentSumPixels === 100) {
            currentSumPixels = 0;
            output[0]++;
        }
    }

    output[1] = currentSumPixels || 100;
    if (!currentSumPixels) output[0]--;

    return output;
};
