var numberOfLines = function (widths, s) {
  let lines = 0;
  let lastLine = 0;

  for (let i = 0; i < s.length; i++) {
    let wid = widths[s[i].charCodeAt(0) - 97];

    if (lastLine + wid <= 100) {
      lastLine += wid;
    } else if (lastLine + wid > 100) {
      lastLine = wid;
      lines++;
    }

    if (lastLine === 100 && i !== s.length - 1) {
      lines++;
      lastLine = 0;
    }
  }
  if (lastLine <= 100) lines++;
  return [lines, lastLine];
};

