impl Solution {
    pub fn number_of_lines(widths: Vec<i32>, s: String) -> Vec<i32> {
        if s.len() == 0 {
            return vec![0, 0];
        }

        let (line, acc) = s
            .chars()
            .map(|chr| alpha_index(chr))
            .map(|chr_ind| widths[chr_ind])
            .fold((1, 0), |(line, acc), cur| {
                if acc + cur > 100 {
                    (line + 1, cur)
                } else {
                    (line, acc + cur)
                }
            });

        vec![line, acc]
    }
}

fn alpha_index(chr: char) -> usize {
    if chr >= 'a' && chr <= 'z' {
        (chr as usize) - ('a' as usize)
    } else {
        panic!("unsupported character")
    }
}
