        for (int i = 0; i < s.size(); ++i) {
            if (curr + widths[s[i] - 97] > 100) {
                curr = 0;
                result[0]++;
            }
            curr += widths[s[i] - 97];
        }
        result[1] = curr;
        return result;
    }
};
