        for i := 0; i < len(word); i++ {
            currentCharSet[int(word[i] - 'a')]--

            if currentCharSet[int(word[i] - 'a')] < 0 {
                isValid = false
                break
            }
        }

        if isValid {
            sum += len(word)
        }
    }

    return sum
}
