public int countCharacters(String[] words, String chars) {
	int count = 0;
	int[] seen = new int[26];
	for(char c: chars.toCharArray()) {
		seen[c - 'a']++;
	}

	for(String word: words) {
		// copy the seen array so we don't have to modify the original seen array
		int[] seenCopy = Arrays.copyOf(seen, seen.length);
		boolean isEarlyReturn = false;
		for(char c: word.toCharArray()) {                
			seenCopy[c - 'a']--;
			if(seenCopy[c - 'a'] < 0) {
				isEarlyReturn = true;
				break;
			}
		}
		if(!isEarlyReturn)
			count = count + word.length();
	}
	return count;
}
