    public int countCharacters(String[] words, String chars) {
        int[] arr = new int[26];
        for (char c : chars.toCharArray()) {
            arr[c-'a']++;
        }
        int ans = 0;
        for(String w : words) {
            int[] charArray = arr.clone();
            int count = 0;
            for (char c : w.toCharArray()) {
                if(charArray[c - 'a'] > 0) {
                    charArray[c - 'a']--;
                    count++;
                } else 
                    break;                
            }
            if(count == w.length()) 
                ans += w.length();
        }
        return ans;
    }
}
