class Solution {
public:

    int countCharacters(vector<string>& words, string chars) {

        // hash table of chars and their count.
        int charsCounts[26] = {0};
        for(int i = 0; i < chars.length(); i++){
            charsCounts[chars[i] - 'a']++;
        }

        // For each word assume its valid and add its length to result.
        // Alternative is to use a flag.
        // For each word create its own hashtable of char count.
        // Add all letters from the word into a hash table.
        // After a letter is added check if it's count exceeds the count
        // of the same letter in charsCount hash table.
        // If it does the assumption was wrong so substract the words length from result 
        // and continue to the next word.
        int result = 0;
        for(int i = 0; i < words.size(); i++){
            result += words[i].length();
            int wordCharCount[26] = {0};
            for(int j = 0; j < words[i].length(); j++){
                int index = words[i][j] - 'a';
                wordCharCount[index]++;
                if(wordCharCount[index] > charsCounts[index]){
                    result -= words[i].length();
                    break;
                }
            }
        }
        return result;
    }
};

