class Solution {
    public int countCharacters(String[] words, String chars) {
        int[] dict = new int[26];
        int ans = 0;
        for(char c : chars.toCharArray()) dict[c - 'a']++;

        for(String s : words) {
            int[] dictCopy = Arrays.copyOf(dict, 26);
            for(int i = 0; i<s.length(); i++) {
                if(--dictCopy[s.charAt(i) - 'a'] <0 ) break;
                if(i == s.length() - 1) ans += s.length();
            }
        }
        return ans;
    }
