func countCharacters(_ words: [String], _ chars: String) -> Int {
	var charToFreq = [Character: Int]()
	var result = 0

	for char in chars {
		charToFreq[char, default: 0] += 1
	}

	for word in words {
		var curCharToFreq = [Character: Int]()

		for char in word {
			curCharToFreq[char, default: 0] += 1
		}

		var canBeFormed = true
		for (char, freq) in curCharToFreq {
			if charToFreq[char] == nil || charToFreq[char]! < freq { 
				canBeFormed = false
				break 
			}
		}

		if canBeFormed {
			result += word.count
		}
	}

	return result
}
```

