func countCharacters(_ words: [String], _ chars: String) -> Int {
	var freqs = Array(repeating: 0, count: 26)
	var result = 0

	for char in chars {
		freqs[charToInt(char)] += 1
	}

	for word in words {
		var curFreqs = freqs


		var canBeFormed = true
		for char in word {
			let index = charToInt(char)
			if curFreqs[index] <= 0 { 
				canBeFormed = false
				break 
			}
			curFreqs[index] -= 1
		}

		if canBeFormed {
			result += word.count
		}
	}

	return result
}

func charToInt(_ char: Character) -> Int {
	return Int(char.asciiValue! - Character("a").asciiValue!)
}
