class Solution {
    public int countCharacters(String[] words, String chars) {
        int sum = 0;

		//26 size array because all inputs are lowercase english letters
		//prepare char count array using the private method
        int[] charsCount = getCountArray(chars);

        for (String word : words) {
		//prepare char count array for each word in the words list
            int[] wordCount = getCountArray(word);

			//check if the word can be formed by characters in the given "chars" string
            if(isWordPossible(charsCount,wordCount)) {
			//if yes, add the current word's length to the sum
                sum += word.length();
            }
        }

        return sum;
    }

    private static int[] getCountArray(String w) {
        int[] counts = new int[26];
        for(int i = 0; i < w.length(); i++) {
            counts[w.charAt(i) - 'a']++;
        }
        return counts;
    }

    private boolean isWordPossible(int[] cCount, int[] wCount) {
        for(int i = 0; i < cCount.length; i++) {
            if(cCount[i] - wCount[i] < 0)
                return false;
        }
        return true;
    }
}
