var countCharacters = function(words, chars) {
    const hash = chars.split('').reduce((res, c) => {
        if (!res[c]) {
            res[c] = 0;
        }

        res[c]++;

        return res;
    }, {});

    const useChar = (hash, c) => {
        if (!hash[c]) {
            return false;
        }

        hash[c]--;

        if (hash[c] === 0) {
            delete hash[c];
        }

        return true;
    };

    return words
        .filter((word) => {
            let i = 0;
            const map = {...hash};

            while (useChar(map, word[i]) && i < word.length) {
                i++;
            }

            return i === word.length;
        })
        .reduce((res, word) => res + word.length, 0);
};
```

