class Solution:
    def countCharacters(self, words, chars):
        d, total = Counter(chars), 0
        for w in words: total += self.helper(w, d.copy())
        return total

    def helper(self, w, d):
        for c in w:
            if c not in d or d[c] == 0: return 0
            else: d[c]-=1
        return len(w)
