            temp = []
            chars_pool = chars[:]
            for char in word:
                if char not in chars_pool:
                    break
                else:
                    temp.append(char)
                    chars_pool.remove(char)
            temp = ''.join(temp)
            if temp == word:
                count += len(word)

        return count
