class Solution {
public:
    int countCharacters(vector<string>& words, string chars) {
	// map to store frequencies of each charater in chars, unordered map because they take less time.
        unordered_map<char, int> mp;
        for(auto c:chars) mp[c]++;
		// auxillary map because we need to maintain the orginal count of character while manipulating according to the need of the string in words.
        unordered_map<char, int> mp1=mp;
		// flag variable to keep track if we have considered all the element of a particular string in words or not
        int ans=0, flag=0;
        for(auto s:words){
            for(int i=0; i<s.size(); i++){
                if(mp1.find(s[i])!=mp1.end() && mp1[s[i]]!=0){
                    mp1[s[i]]--;
                }
                else{
                    flag=1;
                    break;
                }
            }
            if(flag==0) {
                ans+=s.size();
            }
            else flag=0;
            mp1=mp;
        }
        return ans;
    }
};
