var countCharacters = function(words, chars) {
    // make a map of the characters and their count
    var available = new Map()
    var total = 0
    // populate the available characters map 
    for(var i = 0; i < chars.length; ++i){
        if (!available.has(chars[i])){
            available.set(chars[i], 1)
        } else {
            available.set(chars[i], available.get(chars[i])+1)
        }
    }
    // iterrate through all the words 
    for(var word of words){
        // if the length of the word is greater then char length then skip
      if (word.length > chars.length) continue
        // create a map of the current word and a flag to let us know if we should 
        // commit that to the total 
        var used = new Map()
        var shouldCommit = false
        // iterate through every character and add it to the used map 
        for (var char of word){
            // if available has it then add it to used 
            if (available.has(char) && !used.has(char)){
                used.set(char, 1)
                shouldCommit = true
            } 
            // if both have it make sure we dont go over the ammount
            else if (available.has(char) && used.has(char)){
                if (used.get(char) < available.get(char)){
                     used.set(char,used.get(char)+1)
                    shouldCommit = true
            // if we are going over the ammount available then break and dont commit
                }else if (used.get(char) >= available.get(char)){
                    shouldCommit = false
                    break
                }

            } 
            // if available does not have it then break early and dont commit 
            else {
                shouldCommit = false
               break
            }  

        }
        // commit because the word can be made
        if (shouldCommit == true) total+= word.length

    }
    // return the total 
    return total
}
