class Solution {
	/**
	* 1. Parse chars and count how many a,b,c are and store them in an array [26] (a-z)
	* 2. For each word initialize an empty int[26] and count one letter at a time, storing the result in the new int[].
	*    If the stored result it > than the chars array for that char, return 0 immediately;
	*    If each loop runs till the end, return the word length
	*/
    public int countCharacters(String[] words, String chars) {
		int countWords = 0;
        int[] charCount = getCharCount(chars);
        for(String word : words) {
            countWords += getValidWordLength(word, charCount);
        }
        return countWords;
    }

    private int[] getCharCount(String chars) {
        int[] charCount = new int['z' - 'a' + 1];
        for (int i = 0; i < chars.length(); i++) {
            charCount[chars.charAt(i)- 'a']++;
        }
        return charCount;
    }

    private int getValidWordLength(String word, int[] charCount) {
        int[] wordChars =  new int[26];
        int currentChar;
        for (int i = 0; i < word.length(); i++) {
            currentChar = word.charAt(i) - 'a';
            wordChars[currentChar]++;
            // if current word has more letters [i] than the charCount/given dictionary, return 0 immediately
			if (wordChars[currentChar] > charCount[currentChar]) {
                return 0;
            }
        }
        return word.length();
    }
}
