class Solution {
    public int numWays(String[] words, String target) {
        //strategy: Bottom Up DP
        long mod = 1000000007;
        long[] dp = new long[target.length()];
        //first for loop deals with the current letter in words
        for (int i = 0; i < words[0].length(); i++) {
            //set up frequency array to keep track of the frequence at the ith position
            int[] frequency = new int[26];
            //update the letter frequency 
            for (int j = 0; j < words.length; j++) {
                frequency[words[j].charAt(i) - 'a']++;
            }
            //start from back so previous calculated won't affect current
            //third for loop deals with the current position in target
            for (int j = Math.min(i, target.length() - 1); j >= 0; j--) {
                //see if have any letters that correspond with the jth position in target
                if (frequency[target.charAt(j) - 'a'] > 0) {
                    //two possibilities: 
                    //first position will always just be the frequency because there is no previous to work with since it would be out of bounds
                    //other positions is a combination of previous jth position in target and current frequency
                    dp[j] += (j == 0) ? frequency[target.charAt(j) - 'a'] : dp[j - 1] * frequency[target.charAt(j) - 'a'];
                    dp[j] %= mod;
                }
            }
        }
        return (int) dp[target.length() - 1];
    }
}

