class Solution:
    def numWays(self, words: List[str], target: str) -> int:
        # Per-index dict of character counts in words.
        charPositionDict = defaultdict(lambda: defaultdict(int))
        for word in words:
            for idx, c in enumerate(word):
                charPositionDict[idx][c]+=1

        # Number of ways to create word[i:] using character indexes k,k+1,...
        @lru_cache(None)
        def dp(i, k):
            # We've reached the end of target word.
            if i == len(target):
                return 1
            # Not enough characters remain, no results possible.
            if len(target) - i - 1 > len(words[0]) - k - 1:
                return 0

            # Number of formations using position k.
            ret=(charPositionDict[k][target[i]]*dp(i+1, k+1))
            # Number of formations not using position k.
            ret+=dp(i, k+1)
            return ret % (10**9 + 7)

        return dp(0, 0)
