// Approach 2: Top-Down DP (Memoization)

// Time complexity: O(n * k + m * k)
// Space complexity: O(m * k)

class Solution {
    int[][] count;
    int MOD = (int) (Math.pow(10, 9) + 7);
    Long[][] memo;

    public int numWays(String[] words, String target) {
        count = new int[26][words[0].length()];
        memo = new Long[words[0].length()][target.length()];

        for (String word : words) {
            for (int i = 0; i < word.length(); i++) {
                count[word.charAt(i) - 'a'][i]++;
            }
        }

        return (int) dfs(target, 0, 0);
    }

    private long dfs(String target, int i, int k) {
        if (k == target.length())
            return 1;

        if (i == count[0].length)
            return 0;

        if (memo[i][k] != null)
            return memo[i][k];

        memo[i][k] = dfs(target, i + 1, k);  
        memo[i][k] += count[target.charAt(k) - 'a'][i] * dfs(target, i + 1, k + 1);

        return memo[i][k] % MOD;
    }
}
```

