        m=target.length();
        arr=new Integer[n][m];
        cnt=new int[n][26];
        for(String word:words){
            for(int i=0;i<n;i++){
                cnt[i][word.charAt(i)-'a']++;
            }
        }
        return rec(0,0);
    }
    public int rec(int i,int j){
        if(j==m)return 1;
        if(i==n)return 0;
        if(arr[i][j]!=null)return arr[i][j];
        int ind=target.charAt(j)-'a';
        int ans=rec(i+1,j);
        if(cnt[i][ind]>0){
            ans=(ans+((rec(i+1,j+1))%mod*cnt[i][ind])%mod)%mod;
        }
        return arr[i][j]=ans;
    }
}
```

