class Solution {
    int mod=1000000007;
    public int numWays(String[] words, String target) {
        return numWays(words,target,0,0);
    }
    public int numWays(String[] words, String target, int currentWord, int targetIndex) {
        if(targetIndex>=target.length()){
            return 1;
        }
        if(currentWord>=words[0].length() ||  (words[0].length()-currentWord)<(target.length()-targetIndex)){
              return 0;
          }
        long count=0;
        long notInclude=(numWays(words,target,currentWord+1,targetIndex))%mod;
        for(int i=0; i<words.length ; i++){
            if(words[i].charAt(currentWord)==target.charAt(targetIndex)){
               count++;
            }
        }
        long include=((count%mod)*(numWays(words,target,currentWord+1,targetIndex+1))%mod)%mod;
