    public long rec(int j,int i,String[] words,String target,int n,int m,long[][] dp,int[][] fre)
    {
        if(i==m) // i runs on target
            return 1;
        if(j==n) // j runs on column number of words array
            return 0;
        if(dp[j][i]!=-1)
            return dp[j][i];
        long pick=0;
