                dp[i][ch] = (dp[i-1][ch-1] * 
                    charFrequency[ch-1][target.charAt(i - 1)-'a']% MOD + dp[i][ch-1])%MOD;
            }
        }
        System.out.println(Arrays.deepToString(dp));

        return (int) dp[len][max];
    }
}
