        if (k == target.length())
            return 1;

        if (i == count[0].length)
            return 0;

        long res = dfs(target, i + 1, k);  
        res += count[target.charAt(k) - 'a'][i] * dfs(target, i + 1, k + 1);

        return res % MOD;
    }
