class Solution {
public:
    int mod = 1000000007;
    int solve(int targetindex, int wordindex,vector<string>& w, string &t)
    {
        if(targetindex >= t.length())
            return 1;
        if(wordindex >= w[0].length())
        {
            return 0;
        }

        long long int notpick = solve(targetindex,wordindex+1,w,t)%mod;
        int tot = 0;
        for(int i=0;i<w.size();i++)
        {
            if(t[targetindex] == w[i][wordindex])
                tot++;
        }

        long long int pick = (tot%mod)*(long long int)solve(targetindex+1,wordindex+1,w,t)%mod;

        return (pick+notpick)%mod;


    }

    int numWays(vector<string>& w, string t) {

        return solve(0,0,w,t);
    }
};
