class Solution {
public:

    int getWays(vector<vector<int>> &memo,vector<vector<int>> &ip,string &tar,int k,int j){
        if(memo[k][j]==-1){
            if(k==0) return 1;
            if(j==0) return 0;
            memo[k][j]=0;
            for(int i=j-1;i>=k-1;i--){
                memo[k][j]=(memo[k][j]+((long long)ip[i][tar[k-1]-'a']*getWays(memo,ip,tar,k-1,i)%1000000007))%1000000007;
            }
        }
        return memo[k][j];
    }

    int numWays(vector<string>& wod, string tar) {
        int k=wod[0].size()-tar.length()+1;
        if(k<=0) return 0;
        else{
            vector<vector<int>> ip(wod[0].size(),vector<int>(26,0));
            for(int i=0;i<wod.size();i++){
                for(int j=0;j<wod[0].size();j++){
                    ip[j][wod[i][j]-'a']++;
                }
            }
            vector<vector<int>> memo(tar.length()+1,vector<int>(ip.size()+1,-1));
            return getWays(memo,ip,tar,tar.length(),ip.size());
        }
    }
};
