class Solution {
public:
    int mod=1000000007;
     int solveRec(int ind,int x,vector<vector<int>>&arr,string target){

            if(ind==target.size()) return 1;
            if(arr.size()-x<target.size() -ind) return 0;
             long long  ans=solveRec(ind,x+1,arr,target);

             if(arr[x][(target[ind]-'a')]){
                int cnt= arr[x][target[ind]-'a'];
                ans=(ans+(cnt*1ll*solveRec(ind+1,x+1,arr,target))%mod)%mod;
              }

            return (int)ans%mod;
    }

    int solveMemo(int ind,int x,vector<vector<int>>&arr,string target,vector<vector<int>>&dp){

            if(ind==target.size()) return 1;
            if(arr.size()-x<target.size() -ind) return 0;
            if(dp[ind][x]!=-1) return dp[ind][x];
             long long  ans=solveMemo(ind,x+1,arr,target,dp);

            if(arr[x][(target[ind]-'a')]){
                int cnt= arr[x][target[ind]-'a'];
                ans=(ans+(cnt*1ll*solveMemo(ind+1,x+1,arr,target,dp))%mod)%mod;
            }


