#define ll long long 
#define mod 1000000007
class Solution {
public:
    int dp[1001][1001];
    int f[1001][26];
    int helper(vector<string>& v, string &s,int i,int n,int j,int m){
        if(j==m) return 1;
        if(i==n) return 0;
        if(dp[i][j]!=-1) return dp[i][j];
        ll ans=0;
        ans=(ans+f[i][s[j]-'a']*(ll)helper(v,s,i+1,n,j+1,m)%mod)%mod;
        ans=(ans+helper(v,s,i+1,n,j,m)%mod)%mod;
        return dp[i][j]=(ans%mod);
    }
    int numWays(vector<string>& v, string s) {
        int n=v[0].size();
        int m=s.size();
        memset(dp,-1,sizeof(dp));
        memset(f,0,sizeof(f));
        for(int i=0;i<v.size();i++){
            for(int j=0;j<n;j++){
                f[j][v[i][j]-'a']++;
            }
        }
        return helper(v,s,0,n,0,m);
    }
};
