class Solution(object):
    def numWays(self, words, target):
        """
        :type words: List[str]
        :type target: str
        :rtype: int
        """
        mod = 10**9+7
        m = len(words[0])
        n = len(target)
        freq = [defaultdict(int) for _ in range(m)]
        for word in words: 
            for i, char in enumerate(word): 
                freq[i][char] += 1
        memo = {}

        def dfs(i, j):
            # return number of ways to form target[i:] at col j
            if (i, j) in memo:
                return memo[(i,j)]
            if i == n:
                return 1
            if j == m:
                return 0

            res = (dfs(i, j+1) + dfs(i+1, j+1)*freq[j][target[i]]) % mod
            memo[(i, j)] = res

            return res

        return dfs(0, 0)

