    public class Solution
    {
        private const int MODULO = 1000000007;


        public int NumWays(string[] words, string target)
        {
            checked
            {
                int m = words[0].Length;
                List<int>[] indices = new List<int>[26];
                IDictionary<int, int>[] maps = new IDictionary<int, int>[26];
                for (int i = 0; i < 26; i++)
                {
                    indices[i] = new List<int>();
                }

                foreach (var word in words)
                {
                    for (int i = 0; i < word.Length; i++)
                    {
                        indices[word[i] - 'a'].Add(i);
                    }
                }

                for (int i = 0; i < 26; i++)
                {
                    IDictionary<int, int> map = new Dictionary<int, int>();
                    foreach (var idx in indices[i])
                    {
                        if (!map.ContainsKey(idx))
                        {
                            map[idx] = 0;
                        }

                        map[idx]++;
                    }

                    maps[i] = map;
                }

                int[,] dp = new int[target.Length + 1, m];

                for (int i = 0; i < m; i++)
                {
                    dp[0, i] = 1;
                }

                for (int len = 1; len <= target.Length; len++)
                {
                    var map = maps[target[len - 1] - 'a'];
                    for (int idx = 0; idx < m; idx++)
                    {
                        if (idx != 0)
                        {
                            dp[len, idx] = dp[len, idx - 1];
                            if (map.ContainsKey(idx))
                            {
                                dp[len, idx] += (int)(((long) dp[len - 1, idx - 1] * (long) map[idx]) % MODULO);
                                dp[len, idx] %= MODULO;
                            }
                        }
                        else
                        {
                            if (len == 1 && map.ContainsKey(idx))
                            {
                                dp[len, idx] = map[idx];
                            }
                        }

                    }
                }


                return dp[target.Length, m - 1];
            }
        }
    }
