class Solution:
    def numWays(self, words: List[str], target: str) -> int:

        # counts[i] is a hash-map that holds the frequencies for letters at position i in each word
        counts = [collections.Counter(w[i] for w in words) for i in range(len(words[0]))]

        # Now, we find every path through counts that uses no more than len(words[0]) - len(targets) "skips"
        # A skip means we do not include counts[ci][target[ti]] in the intermediate result for this path
        ans = 0
        @functools.lru_cache(None)
        def dfs(ci, skips):   # ci -> counts index
            # Target index is ci - the number of skips we've used
            ti = ci-skips
            if ti == len(target):
                return 1

            # This means we couldn't create the target string using this path
            if ci == len(counts):
                return 0

            res = counts[ci][target[ti]] * dfs(ci+1, skips)
            if skips < len(words[0])-len(target):
                res += dfs(ci+1, skips+1)

            return res

        return dfs(0, 0) % (10**9 + 7)
