class Solution {
    int m;
    int n;
    int freq[][];
    int MOD=1000000007;
    int memo[][];
    public int numWays(String[] words, String target) {
       freq=new int[26][words[0].length()];
       m=target.length();
       n=words[0].length();
       memo=new int[m][n];
       for(int mem[]:memo)Arrays.fill(mem,-1);
       for(int i=0;i<words.length;i++){
           for(int j=0;j<n;j++){
               char ch=words[i].charAt(j);
               freq[ch-'a'][j]++;
           }
       }
       return (int)solve(0,0,target);
    }
    public long solve(int i,int j,String target){
           if(i==m)return 1;

           if(j==n)return 0;
           if(memo[i][j]!=-1)return memo[i][j];
           int nottaken=(int)solve(i,j+1,target)%MOD;
           int taken=(int)((long)freq[target.charAt(i)-'a'][j]*solve(i+1,j+1,target)%MOD);

           return memo[i][j]= (nottaken+taken)%MOD;
       }


}
