    def numWays(self, words: List[str], target: str) -> int:
        dp = [[-1 for j in range(len(target))] for i in range(len(words[0]))]
        letterFreq = [[0 for j in range(26)] for i in range(len(words[0]))]
        for i in range(len(words[0])):
            for word in words:
                letterFreq[i][ord(word[i])-97] += 1

        def dfs(k, j):
            if j==len(target):
                return 1
            if k==len(words[0]):
                return 0
            if dp[k][j] > -1:
                return dp[k][j]
            dp[k][j] = dfs(k+1 , j) + letterFreq[k][ord(target[j])-97] * dfs(k+1, j+1)
            return dp[k][j]
        return dfs(0, 0) % (10**9+7)

