/**
 * @param {string[]} words
 * @param {string} target
 * @return {number}
 */
var numWays = function(words, target) {
    // Count characters at each idx of word
    const dict = {};
    for (let i = 0 ; i < words[0].length ; i++) {
        const count = Array(26).fill(0);

        for (let j = 0 ; j < words.length ; j++) {
            const c = words[j][i];
            const idx = c.charCodeAt(0) - 'a'.charCodeAt(0);

            count[idx] += 1;
        }

        dict[i] = count;
    }

    const cache = [];
    for (let i = 0 ; i < words[0].length ; i++) {
        cache.push(Array(target.length).fill(null));
    }

    return find(cache, dict, target, words[0].length, 0, 0);
};

function find(cache, dict, target, maxWordLength, charIdx, targetIdx) {
    if (targetIdx >= target.length) {
        return 1;
    }

    if (charIdx >= maxWordLength) {
        return 0;
    }

    if (cache[charIdx][targetIdx] !== null) {
        return cache[charIdx][targetIdx];
    }

    // Skip this charIdx
    let count = find(cache, dict, target, maxWordLength, charIdx + 1, targetIdx);

    const wanted = target[targetIdx];
    const idx = wanted.charCodeAt(0) - 'a'.charCodeAt(0);

    // Use this charIdx
    if (dict[charIdx][idx] > 0) {
        count += dict[charIdx][idx] * find(cache, dict, target, maxWordLength, charIdx + 1, targetIdx + 1);
    }

    cache[charIdx][targetIdx] = count % 1000000007;
    return cache[charIdx][targetIdx];
}
