using LL = long long;
class Solution {
public:
    int numWays(vector<string>& words, string target) {
        int alp_size = 26;
        int target_len = target.size();
        int word_len = words[0].size();
        int M = 1e9 + 7;

        vector<vector<int>> char_col_freq(alp_size, vector<int>(word_len + 1, 0));

        for(auto& word: words){
            for(int i = 1; i <= word_len; ++i){
                char c = word[i-1];
                char_col_freq[c-'a'][i] ++;
            }
        }

        vector<vector<LL>> dp(target_len + 1, vector<LL>(word_len + 1, 0));
        for(int i = 0; i <= word_len; ++i){
            dp[0][i] = 1;
        }

        for(int tlen = 1; tlen <= target_len; ++tlen){
            for(int col = 1; col <= word_len; ++col){
                if(tlen > col)
                    continue;
                char c = target[tlen-1];
                dp[tlen][col] = dp[tlen][col-1] + dp[tlen-1][col-1] * char_col_freq[c-'a'][col];
                dp[tlen][col] %= M;
            }
        }

        return (int)dp[target_len][word_len];
    }
};
