public class Solution {
    public int NumWays(string[] words, string target) {
        int mod = ((int)1e9) + 7;
        int n = words[0].Length,m = target.Length;
        int[][] dp = new int[n][];
        for(int i = 0; i < n;i++){
            dp[i] = new int[m];
            Array.Fill(dp[i],-1);
        }
        int solve(int i, int j){
            if(j >= m) return 1;
            if(i >= n || m - j > n - i) return 0;
            if(dp[i][j] != -1)return dp[i][j];
            int res = solve(i + 1,j) % mod;
            foreach(var word in words){
                if(word[i] == target[j])
                    res = (res % mod +  solve(i + 1,j + 1) % mod) % mod;
            }
            return dp[i][j] = res % mod;
        }
        return solve(0,0);
    }
}
```

