long long hope(int index,int prev,vector<vector<long long>>&freq,string &target,int &len,vector<vector<long long>>&dp){
    if(index==target.length())return 1;
    else if(index<target.length()-1 && prev>=len)return 0;
    else if(target.length()-index>(len-prev))return 0;
    if(dp[index][prev+1]!=-1)return dp[index][prev+1];
    long long ans=0;
