class Solution:
    def numWays(self, words: List[str], target: str) -> int:
        oc = Counter()
        for word in words:
            for i, c in enumerate(word):
                oc[(c, i)] += 1
        m, n = len(words[0]), len(target)
        D = [[0 for _ in range(m)] for _ in range(n)]
        for i in range(n):
            for j in range(m):
                if i > j:
                    continue
                elif i == 0 and j == 0:
                    D[i][j] = oc[(target[0], 0)]
                elif i == 0:
                    D[i][j] = D[i][j - 1] + oc[(target[i], j)]
                else:
                    D[i][j] = D[i][j - 1] + D[i - 1][j - 1] * oc[(target[i], j)]
        return D[n - 1][m - 1] % (10**9 + 7)
