        target.reverse();
        freq_map.reverse(); // WHY DO YOU HATE YOURSELF

        let dp_len = n - m + 1;
        let mut dp = VecDeque::from(vec![1usize; dp_len]); // magic

        for (i, c) in target.into_iter().enumerate() { // what??
            let mut count_prev = 0usize;

            for j in (0..(n-m+1)) { // ??
                let mut head = dp.pop_front().unwrap();

