```cpp []
class Solution {
private:
    vector<vector<unsigned long>> preproc(const vector<string>& words) {
        auto n = words[0].size();

        vector<vector<unsigned long>> vec;

        for (auto i = 0; i < n; i += 1) {
            vec.emplace_back(26, 0);
        }

        for (auto word : words) {
            for (auto i = 0; i < word.size(); i += 1) {
                auto c = word[i];
                vec[i][c - 'a'] += 1;
            }
        }

        return vec;
    }

public:
    int numWays(vector<string>& words, string target) {
        if (words.empty()) return 0;

        auto freq_map = preproc(words);
        auto m = target.size();
        auto n = freq_map.size();

        if (m > n) return 0;

        reverse(begin(freq_map), end(freq_map));
        reverse(begin(target), end(target));

        unsigned long MOD = 1000000007;

        auto dp_len = n - m + 1;
        deque<unsigned long> dp;
        for (auto i = 0; i < dp_len; i += 1) {
            dp.push_back(1);
        }

        for (auto i = 0; i < target.size(); i += 1) {
            int c = target[i] - 'a';

            unsigned long count_prev = 0;

            for (auto j = 0; j < n-m+1; j += 1) {
                auto count_next = dp.front();
                dp.pop_front();

                count_next *= freq_map[i+j][c];
                count_next += count_prev;
                count_next %= MOD;

                count_prev = count_next;
                dp.push_back(count_next);
            }
        }

        return dp.back();
    }
};
